import React,{useState} from "react";
import Data from"../conditions.json";
import Popup from "./Popup"
import "./style.css";


export default function ConditionsList(props) {
  const conditions = Data.conditions;
  const [showPopup,setShowPopup] = useState(false)
  return (
    <div className="container">
      {conditions.map(condition => {
        return (
          <div className="condition">
            <img className="image" src={condition.image}></img>
            <div className="title">{condition.label}</div>
            <div className="snippet">{condition.snippet}</div>
            <link className="more" onClick={() => setShowPopup(true)}>...Find out more</link>
            <Popup trigger={showPopup}>
              <img className="image" src={condition.image}></img>
              <div className="title">{condition.label}</div>
              <div className="text">{condition.snippet}</div>
              <div className="text">{condition.synonyms}</div>
              <div className="text">{condition.keywords}</div>
            </Popup>
          </div>)
      })}

    </div>
  )}
