import React,{useState} from "react";
import Popup from "./Popup"
import "./style.css";


export default function Condition(props) {
  const condition = props.condition;
  const [showPopup,setShowPopup] = useState(false)

        return (
          <div className="condition">
            <img className="image" src={condition.image}>{condition.image ? "" : condition.label}</img>
            <div className="title">{condition.label}</div>
            <div className="snippet">{condition.snippet}</div>
            <button className="more-btn" onClick={() => setShowPopup(true)}>...Find out more</button>
            <Popup trigger={showPopup} setTrigger={setShowPopup} condition={condition}></Popup>
          </div>
        )}
